package simplepgm;
//wap to number is even or odd
public class even1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		for( int i=a;i<=b;i++)
		{
			if(i%2==0)
			{
				System.out.println(i+ " is even number");
			}
			else
			{
				System.out.println(i+ " is  number");
			}
				
		}

	}

}
